 import React , {useState} from "react";
 import "./ExpenseForm.css";
 

 
 const ExpenseForm = (props) => {
  const [enteredTitle , setEnteredTitle] = useState('');

  const [enteredAmount , setEnteredAmount] = useState('');

  const [enteredDate , setEnteredDate] = useState('');





// const [userInput , setUserInput]= useState({
//   enteredTitle: '',

//   enteredAmount: '',
//   enteredDate:'',

//})
    const titleChangeHandler = (event) =>{
    //    setUserInput({
    setEnteredTitle(event.target.value);
    //     ...userInput,
    //     enteredTitle: event.target.value,
    //    })
        
     };
       
        //  setUserInput((prevState) => {    // safer way to ensure that you always operate on the latest state snapshot.
        //   return {...prevState , enteredTitle:event.targte.value };

        //  })

    const amountChangeHandler = (event)=>{
          setEnteredAmount(event.target.value);
          // setUserInput({
          //   ...userInput,
          //   enteredAmount: event.target.value,
          // })
    };

    const dateChangeHandler = (event) =>{
      setEnteredDate(event.target.value);
    //   setUserInput({
    //     ...userInput,
    //     enteredDate:event.target.value,
    //   })
    // }
          
    // const inputChangeHandler = (identifier , value) => {
    //   if (identifier === 'title') {
    //     setEnteredTitle(value);
    //   }else if (identifier === 'date'){
    //     setEnteredDate(value);
    //   }else{
    //     setEnteredAmount(value);
    //   }

    // };
    };
         const submitHandler = (event) => {
           event.preventDefault(); // built into javascript behaviour. nothing on react
           // preventing the default of this request being sent and the page will not reload.
          
           const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date : new Date(enteredDate),
           }

           
           props.onSaveExpenseData(expenseData);
           setEnteredTitle('');    //These lines clear the form input fields (or state variables) after the form data has been processed and saved. 
           //This is often done to reset the form to its initial state after a submission.
           setEnteredAmount('');
           setEnteredDate('');




         };

      
      

      

    return(
    
        <form onSubmit = {submitHandler} >
        <div className='new-expense__controls'>
          <div className='new-expense__control'>
            <label>Title</label>
            <input 
            type='text'
            value ={enteredTitle}
            onChange={titleChangeHandler} />
          </div>
          <div className='new-expense__control'>
            <label>Amount</label>
            <input 
            type='number' 
            min='0.01'
             step='0.01'
             value = {enteredAmount}
              onChange = {amountChangeHandler}/>
          </div>
          <div className='new-expense__control'>
            <label>Date</label>
            <input
             type='date' 
             min='2019-01-01' 
             max='2022-12-31' 
             value = {enteredDate}
             onChange = {dateChangeHandler} />
          </div>
        </div>
        <div className='new-expense__actions'>
        <button type='button' onClick = {props.onCancel}>Cancel</button>
        
          <button type='submit'>Add Expense</button>
          
        </div>
      </form>
     
       
    )

    
    
    };
 
 export default ExpenseForm;
